INSERT INTO `zk_content_info` VALUES ('ff808081519e6dd20151ae9d38200074', '2006年', '2015-12-17 14:25:18', '<html>\r\n<head>\r\n	<title></title>\r\n</head>\r\n<body>&bull; 中国科学院山西煤炭化学研究所、内蒙古伊泰集团有限公司、神华集团有限责任公司、山西潞安矿业（集团）有限责任公司、徐州矿务集团有限公司等共同投资组建了中科合成油技术有限公司（Synfuels China）。&nbsp;<br />\r\n&bull; 开展了第五代先进高效的煤基合成油技术独创的高温浆态床技术研究。<br />\r\n&bull; 中科合成油工程有限公司、中科合成油催化剂有限公司成立。<br />\r\n&bull; 采用公司自主技术的内蒙伊泰集团和山西潞安集团16万吨/年煤基合成油示范项目相继开工建设。</body>\r\n</html>\r\n', '1', '2006年', '402881e851378060015137ad466c0015', '6', '1', '', '', '');
