INSERT INTO `zk_content_info` VALUES ('ff808081515b3fc401515b7246b00045', '水处理技术研究', '2015-12-01 10:49:54', '\r\n<p>煤化工过程中会产生许多类型的废水，如气化黑水、费托合成水以及催化剂生产废水等。</p>\r\n\r\n<p>这些废水都需要有效的处理以满足经济性和环保的要求。 除了传统的精馏、氧化降解等水处理方法，</p>\r\n\r\n<p>中科合成油技术有限公司开展了新型水处理技术包括冷冻浓缩法水处理工艺、水合物法水处理工艺、热泵法水处理工艺的研究开发工作。</p>\r\n', '1', '', '402881e851378060015138376cbe00a2', '1', '1', '', 'http://114.242.195.204:81/upload/files/images/waterTre.png', null);
