INSERT INTO `zk_content_info` VALUES ('ff808081515b3fc401515cee0fbf0267', '冷冻浓缩工艺', '2015-12-01 17:44:44', '中科合成油技术有限公司冷冻浓缩法水处理工艺是采用固液平衡原理分离水与水中的杂质。该工艺过程为: 废水经过冷却后与低温的制冷剂在特定设备内直接接触传热。水冷却生成冰晶。冰晶经过再次长大、冲洗后获得纯度较高的冰晶。剩余的浓缩液部分与进料混合，部分排出系统，经过其它工艺进一步处理。', '1', '', '402881e85137806001513838b0b700b0', '1', '1', '', '', null);
