INSERT INTO `zk_content_info` VALUES ('ff808081515b3fc401515ca956f1014e', '2001年', '2015-12-01 16:29:40', '<html>\r\n<head>\r\n	<title></title>\r\n</head>\r\n<body>&bull; 在实验室研究工作基础上，中国科学院山西煤炭化学研究所合成油品工程研究中心承担了中国科学院重大科技攻关项目、科技部863高科技项目煤基合成液体燃料浆态床工业化技术的研究工作，并与国内企业联合启动了工业煤基合成油中间试验平台的建设工作。</body>\r\n</html>\r\n', '1', '2001年', '402881e851378060015137adf0d20018', '5', '1', '', '', '');
