INSERT INTO `zk_content_info` VALUES ('ff808081515b3fc401515d39c31e0369', '6400吨液压复式起重机研发项目成功启动', '2015-12-01 19:07:25', '2011年7月24日，中科合成油技术有限公司、太原重型机械集团有限公司、中化二建集团有限公司三家企业在位于北京市怀柔区的中科合成油技术有限公司总部研发大楼隆重举行了6400吨液压复式起重机研发项目启动仪式。', '1', '', '402881e851378060015137b8c3470052', '1', '1', '', 'http://114.242.195.204:81/upload/files/images/DSC00090.JPG', '2011-7-24');
