INSERT INTO `zk_en_content_info` VALUES ('ff808081515b3fc401515cb36978018d', '', '2015-12-01 16:40:40', '<html>\r\n<head>\r\n	<title></title>\r\n</head>\r\n<body>\r\n<p class=\"MsoNormal\" style=\"mso-margin-top-alt:auto;mso-margin-bottom-alt:auto;\r\nline-height:27.0pt;mso-line-height-rule:exactly;mso-pagination:widow-orphan\">The core of coal indirect liquefaction synthetic liquid fuel technology is the Fischer Tropsch synthesis (call F-T synthesis for short) process, catalyst and reactor technology. The technical advantages and characteristics are by gasification, purification and synthesis, high-quality oil products and chemical raw materials can be made from any combustible carbonaceous organic matter such as coal and biomass, and ultimately providing a feasible technical route by the renewable resources &ndash;biomass to solve social development on organic hydrocarbons needs.<br />\r\nAccording to features of the technical development trend of coal indirect liquefaction process and F-T synthesis process can well cohesive with biomass (especially residue), Synfuels China attaches great attention on innovative R&amp;D of coal indirect liquefaction oil products and chemicals to realize the technical research of coal indirect liquefaction, low-temperature distillation, classification liquefaction and gas turbine power generation, improving the overall energy conversion efficiency of coal liquefaction process; at the same time, Synfuels China combines with the biomass gasification technology research and demonstration projects, actively promoting the application of F-T synthesis technology in the biomass.</p>\r\n</body>\r\n</html>\r\n', '1', '', '402881e851378060015137ac0a6e000d', '1', '1', '', '', '');
INSERT INTO `zk_en_content_info` VALUES ('ff8080815157d84b01515809701d0044', 'Chairman of the Board：Mr. Zhang Donghai', '2015-11-30 18:56:32', '<html>\r\n<head>\r\n	<title></title>\r\n</head>\r\n<body>\r\n<p class=\"MsoNormal\" style=\"margin-top:5.0000pt; margin-bottom:5.0000pt; mso-margin-top-alt:auto; mso-margin-bottom-alt:auto; line-height:27.0000pt; mso-line-height-rule:exactly; \"><span style=\"mso-spacerun:\'yes\'; font-family:\'Times New Roman\'; mso-fareast-font-family:仿宋; font-size:14.0000pt; mso-font-kerning:1.0000pt; \">Mr.&nbsp;Zhang&nbsp;Donghai&nbsp;graduated&nbsp;from&nbsp;the&nbsp;Beijing&nbsp;Farm&nbsp;Management&nbsp;Cadre&nbsp;Institute&nbsp;in&nbsp;1992;&nbsp;won&nbsp;the&nbsp;Beijing&nbsp;international&nbsp;EMBA&nbsp;Master&nbsp;Degree&nbsp;of&nbsp;Peking&nbsp;University&nbsp;in&nbsp;2004;&nbsp;Senior&nbsp;Economist.</span><span style=\"mso-spacerun:\'yes\'; font-family:\'Times New Roman\'; mso-fareast-font-family:仿宋; font-size:14.0000pt; mso-font-kerning:1.0000pt; \"><o:p></o:p></span></p>\r\n\r\n<p class=\"MsoNormal\"><span style=\"mso-spacerun:\'yes\'; font-family:\'Times New Roman\'; mso-fareast-font-family:仿宋; font-size:14.0000pt; mso-font-kerning:1.0000pt; \">Mr.&nbsp;Zhang&nbsp;Donghai&nbsp;joined&nbsp;in&nbsp;Yitai&nbsp;Group&nbsp;in&nbsp;1990&nbsp;and&nbsp;was&nbsp;in&nbsp;different&nbsp;positions,&nbsp;namely,&nbsp;Deputy&nbsp;Director&nbsp;of&nbsp;Yimeng&nbsp;Coal&nbsp;Company&nbsp;in&nbsp;Beijing&nbsp;Office&nbsp;and&nbsp;Deputy&nbsp;Manager&nbsp;in&nbsp;Beijing&nbsp;Transportation&nbsp;Development&nbsp;Corporation;&nbsp;Deputy&nbsp;Manager&nbsp;in&nbsp;Management&nbsp;Company&nbsp;of&nbsp;Yimeng&nbsp;Coal&nbsp;Company;&nbsp;Director&nbsp;of&nbsp;Yitai&nbsp;Group&nbsp;in&nbsp;Beijing&nbsp;Office;&nbsp;Deputy&nbsp;General&nbsp;Manager&nbsp;of&nbsp;Yitai&nbsp;Coal&nbsp;Limited&nbsp;Company;&nbsp;Chairman&nbsp;and&nbsp;General&nbsp;Manager&nbsp;of&nbsp;Yitai&nbsp;Coal&nbsp;Limited&nbsp;Company;&nbsp;Deputy&nbsp;General&nbsp;Manager&nbsp;of&nbsp;Yitai&nbsp;Group;&nbsp;Deputy&nbsp;Secretary&nbsp;of&nbsp;Party&nbsp;Committee,&nbsp;Executive&nbsp;Director&nbsp;of&nbsp;the&nbsp;Board&nbsp;and&nbsp;General&nbsp;Manager&nbsp;of&nbsp;Yitai&nbsp;Group.&nbsp;Under&nbsp;the&nbsp;leadership&nbsp;of&nbsp;Mr.&nbsp;Zhang&nbsp;Donghai,&nbsp;Yitai&nbsp;Group&nbsp;ranked&nbsp;twenty-third&nbsp;in&nbsp;the&nbsp;national&nbsp;coal&nbsp;industry,&nbsp;the&nbsp;realizing&nbsp;successful&nbsp;listing&nbsp;of&nbsp;Yitai&nbsp;Coal&nbsp;Limited&nbsp;Company,&nbsp;and&nbsp;was&nbsp;named&nbsp;as&nbsp;the&nbsp;2005&nbsp;National&nbsp;Labor&nbsp;Model.&nbsp;In&nbsp;April&nbsp;2006,&nbsp;he&nbsp;was&nbsp;selected&nbsp;as&nbsp;Chairman&nbsp;of&nbsp;the&nbsp;Board&nbsp;of</span><span style=\"mso-spacerun:\'yes\'; font-family:\'Times New Roman\'; mso-fareast-font-family:仿宋; font-size:14.0000pt; mso-font-kerning:0.0000pt; \">&nbsp;Synfuels&nbsp;China&nbsp;Technology&nbsp;Co.,&nbsp;Ltd.&nbsp;</span></p>\r\n</body>\r\n</html>\r\n', '1', '', '402881e851378060015137ae7580001a', '1', '1', '', 'http://114.242.195.204:8081/upload/files/images/%E5%85%B3%E4%BA%8E%E6%88%91%E4%BB%AC_%E9%9B%86%E5%9B%A2%E6%A6%82%E5%86%B5_%E7%AE%A1%E7%90%86%E5%B1%82_%E6%9D%8E%E6%B0%B8%E6%97%BA_03.jpg', '');
INSERT INTO `zk_en_content_info` VALUES ('ff808081515b3fc4015160a5026e05d3', '宁夏萨索尔煤制油项目已上报国家能源局', '2015-12-02 11:03:25', '来源：宁夏日报 日期：2010-03-15\r\n\r\n宁夏煤炭预测资源储量1721万吨，探明储量319亿吨，均居全国第六位。其中宁东煤田是国家规划建设的13个大型煤炭基地之一，探明储量275亿吨，占全区探明储量的87%。主要煤种不粘结煤低灰、低硫、特低磷、中高发热量和高化学活性的特点，是理想的动力用煤和气化、液化的优质原料。\r\n \r\n宁夏沙索煤炭间接液化项目是《国家发展改革委关于加强煤制油项目管理有关问题的通知》确定的我国可以继续开展工作的两个煤制油示范工程项目之一，由神华宁夏煤业集团公司和南非沙索合成燃料国际有限公司按各50%的比例，采用中外合资方式建设和运营，建设规模为年转化煤炭2064万吨，年产合成油可达385万吨。目前，项目各项研究报告已完成，相关支持性文件基本齐全，具备了核准条件，项目核准请示文件已上报国家能源局。\r\n \r\n在工业化、城市化进程加速，居民消费结构不断升级和国际制造业大量向我国转移的共同推动下，我国能源需求旺盛，特别是石油的对外依存度不断提高。依托宁东基地的煤炭资源优势建设宁夏沙索煤制油项目，对保障国家能源安全，推进我国煤炭间接液化技术的发展，实现煤制油产业化、规模化发展具有重要示范作用。宁夏代表团建议国家应进一步加大支持力度，尽早核准宁夏沙索煤炭间接液化项目。\r\n\r\n', '1', '', '402881e8513780600151382e6e46006b', '1', '1', '', '', '');
