INSERT INTO `zk_en_content_info` VALUES ('ff808081515b3fc401515c70b7cc0123', 'In 2004', '2015-12-01 15:27:49', '<html>\r\n<head>\r\n	<title></title>\r\n</head>\r\n<body>\r\n<p class=\"MsoNormal\" style=\"mso-margin-top-alt:auto;mso-margin-bottom-alt:auto;\r\nline-height:27.0pt;mso-line-height-rule:exactly;mso-pagination:widow-orphan\"><span lang=\"EN-US\" new=\"\" style=\"font-size:14.0pt;font-family:\" times=\"\">Based on the CAS Synthetic Oil Engineering Research Center, CAS combined with domestic enterprises to establish Synthetic Oil Engineering Center Council, laying the foundation of industrialization and companization of the technology.<o:p></o:p></span></p>\r\n\r\n<p class=\"MsoNormal\" style=\"mso-margin-top-alt:auto;mso-margin-bottom-alt:auto;\r\nline-height:27.0pt;mso-line-height-rule:exactly;mso-pagination:widow-orphan\"><span lang=\"EN-US\" new=\"\" style=\"font-size:14.0pt;font-family:\" times=\"\">- Independent IPR of coal based synthetic oil technology was passed technical appraisal of CAS and Shanxi Provincial Organization.<o:p></o:p></span></p>\r\n</body>\r\n</html>\r\n', '1', 'Year of 2004 ', '402881e851378060015137adf0d20018', '3', '1', '', '', '');
INSERT INTO `zk_en_content_info` VALUES ('ff808081515b3fc401515b4b9227000f', 'Vice Chairman of the Board, General Manager and Chief Scientist： Mr. Li Yongwang', '2015-12-01 10:07:38', '<html>\r\n<head>\r\n	<title></title>\r\n</head>\r\n<body>\r\n<p class=\"MsoNormal\"><span style=\"mso-spacerun:\'yes\'; font-family:\'Times New Roman\'; mso-fareast-font-family:仿宋; font-size:14.0000pt; mso-font-kerning:0.0000pt; \">Mr.&nbsp;Li&nbsp;Yongwang&nbsp;graduated&nbsp;from&nbsp;the&nbsp;Inner&nbsp;Mongolia&nbsp;Institute&nbsp;of&nbsp;Engineering&nbsp;in&nbsp;July&nbsp;1984;&nbsp;from&nbsp;1986&nbsp;to&nbsp;1994,&nbsp;he&nbsp;worked&nbsp;and&nbsp;studied&nbsp;in&nbsp;the&nbsp;CAS&nbsp;Shanxi&nbsp;Coal&nbsp;Chemistry&nbsp;Research&nbsp;Institute,&nbsp;attaining&nbsp;Master&nbsp;Degree&nbsp;of&nbsp;Engineering&nbsp;and&nbsp;Doctor&nbsp;Degree&nbsp;of&nbsp;Science.&nbsp;In&nbsp;1995,&nbsp;Mr.&nbsp;Li&nbsp;Yongwang&nbsp;made&nbsp;postdoctoral&nbsp;research&nbsp;in&nbsp;the&nbsp;Petroleum&nbsp;Research&nbsp;Institute&nbsp;of&nbsp;Belgian&nbsp;GENT&nbsp;University;&nbsp;In&nbsp;1996,&nbsp;he&nbsp;continued&nbsp;his&nbsp;postdoctoral&nbsp;research&nbsp;work&nbsp;in&nbsp;Catalytic&nbsp;Institute&nbsp;of&nbsp;Belgium&nbsp;New&nbsp;Leuven&nbsp;University;&nbsp;from&nbsp;1999&nbsp;to&nbsp;2000,&nbsp;he&nbsp;made&nbsp;cooperative&nbsp;study&nbsp;in&nbsp;the&nbsp;Erlangen&nbsp;Research&nbsp;Institute&nbsp;(funded&nbsp;by&nbsp;Humboldt&nbsp;Foundation).&nbsp;<br />\r\nFrom&nbsp;1997&nbsp;to&nbsp;2006,&nbsp;Professor&nbsp;Li&nbsp;Yongwang&nbsp;was&nbsp;employed&nbsp;by&nbsp;CAS&nbsp;Shanxi&nbsp;Coal&nbsp;Chemistry&nbsp;Research&nbsp;Institute&nbsp;as&nbsp;Deputy&nbsp;Director&nbsp;of&nbsp;State&nbsp;Key&nbsp;Laboratory&nbsp;of&nbsp;Coal&nbsp;Conversion&nbsp;and&nbsp;Director&nbsp;of&nbsp;Synthetic&nbsp;Oil&nbsp;Engineering&nbsp;Research&nbsp;Center,&nbsp;leading&nbsp;the&nbsp;research&nbsp;work&nbsp;of&nbsp;coal&nbsp;based&nbsp;synthetic&nbsp;oil&nbsp;technology,&nbsp;catalytic&nbsp;reaction&nbsp;engineering&nbsp;and&nbsp;computer&nbsp;simulation.&nbsp;In&nbsp;the&nbsp;meantime,&nbsp;Professor&nbsp;Li&nbsp;completed&nbsp;863&nbsp;Project&nbsp;of&nbsp;Ministry&nbsp;of&nbsp;Science&nbsp;and&nbsp;Technology&nbsp;and&nbsp;R&amp;D&nbsp;work&nbsp;of&nbsp;&ldquo;Industrialization&nbsp;Technology&nbsp;of&nbsp;Coal&nbsp;Based&nbsp;Liquid&nbsp;Fuel&nbsp;Synthesis&nbsp;in&nbsp;Slurry&nbsp;Reactor&rdquo;&nbsp;of&nbsp;CAS&nbsp;Key&nbsp;Knowledge&nbsp;Innovation&nbsp;Project;&nbsp;he&nbsp;defined&nbsp;the&nbsp;overall&nbsp;technology&nbsp;scheme&nbsp;of&nbsp;synthetic&nbsp;oil&nbsp;technology&nbsp;in&nbsp;slurry&nbsp;reactor&nbsp;and&nbsp;constructed&nbsp;the&nbsp;first&nbsp;set&nbsp;of&nbsp;Chinese&nbsp;synthetic&nbsp;oil&nbsp;industrial&nbsp;test&nbsp;platform&nbsp;and&nbsp;catalyst&nbsp;production&nbsp;demonstration&nbsp;device;&nbsp;he&nbsp;organized&nbsp;and&nbsp;completed&nbsp;many&nbsp;pilot&nbsp;tests&nbsp;of&nbsp;synthetic&nbsp;oil&nbsp;and&nbsp;edited&nbsp;China&rsquo;s&nbsp;first&nbsp;generation&nbsp;of&nbsp;industrial&nbsp;simulation&nbsp;software&nbsp;of&nbsp;coal-to-oil&nbsp;whole&nbsp;process;&nbsp;he&nbsp;presided&nbsp;over&nbsp;the&nbsp;completion&nbsp;of&nbsp;technical&nbsp;software&nbsp;of&nbsp;0.16&nbsp;million&nbsp;tons/year&nbsp;synthetic&nbsp;oil&nbsp;demonstration&nbsp;plant&nbsp;and&nbsp;finally&nbsp;developed&nbsp;coal&nbsp;based&nbsp;synthetic&nbsp;liquid&nbsp;fuel&nbsp;industrial&nbsp;technology&nbsp;with&nbsp;independent&nbsp;IPR,&nbsp;forming&nbsp;the&nbsp;systematic&nbsp;application&nbsp;technology&nbsp;which&nbsp;can&nbsp;be&nbsp;further&nbsp;industrialized.&nbsp;In&nbsp;2006,&nbsp;Professor&nbsp;Li&nbsp;initiated&nbsp;personnel&nbsp;in&nbsp;enterprise&nbsp;circle&nbsp;and&nbsp;set&nbsp;up&nbsp;Synfuels&nbsp;China&nbsp;Technology&nbsp;Co.,&nbsp;Ltd.&nbsp;In&nbsp;April&nbsp;2006,&nbsp;he&nbsp;was&nbsp;elected&nbsp;as&nbsp;General&nbsp;Manager&nbsp;of&nbsp;Synfuels&nbsp;China&nbsp;and&nbsp;was&nbsp;responsible&nbsp;for&nbsp;R&amp;D&nbsp;work&nbsp;and&nbsp;comprehensive&nbsp;business&nbsp;development&nbsp;of&nbsp;the&nbsp;Company.&nbsp;</span><br />\r\n&nbsp;</p>\r\n\r\n<p class=\"MsoNormal\" style=\"margin-top:5.0000pt; margin-bottom:5.0000pt; mso-margin-top-alt:auto; mso-margin-bottom-alt:auto; line-height:27.0000pt; mso-line-height-rule:exactly; \"><span style=\"mso-spacerun:\'yes\'; font-family:\'Times New Roman\'; mso-fareast-font-family:仿宋; font-size:14.0000pt; mso-font-kerning:0.0000pt; \">In&nbsp;2009,&nbsp;coal&nbsp;based&nbsp;synthetic&nbsp;liquid&nbsp;fuel&nbsp;technology&nbsp;developed&nbsp;by&nbsp;Professor&nbsp;Li&nbsp;and&nbsp;his&nbsp;working&nbsp;team&nbsp;was&nbsp;successfully&nbsp;applied&nbsp;in&nbsp;three&nbsp;coal-to-oil&nbsp;demonstration&nbsp;plants&nbsp;such&nbsp;as&nbsp;Yitai,&nbsp;Lu&rsquo;an&nbsp;and&nbsp;etc.&nbsp;Among&nbsp;which,&nbsp;Inner&nbsp;Mongolia&nbsp;Yitai&nbsp;Group&nbsp;and&nbsp;Shanxi&nbsp;Lu&rsquo;an&nbsp;Coal&nbsp;Group&nbsp;coal-to-oil&nbsp;demonstration&nbsp;plants&nbsp;ran&nbsp;successfully&nbsp;in&nbsp;2009,&nbsp;smoothly&nbsp;producing&nbsp;qualified&nbsp;oil.&nbsp;</span><span style=\"mso-spacerun:\'yes\'; font-family:\'Times New Roman\'; mso-fareast-font-family:仿宋; font-size:14.0000pt; mso-font-kerning:0.0000pt; \"><o:p></o:p></span></p>\r\n\r\n<p class=\"MsoNormal\">&nbsp;</p>\r\n\r\n<p class=\"MsoNormal\" style=\"margin-top:5.0000pt; margin-bottom:5.0000pt; mso-margin-top-alt:auto; mso-margin-bottom-alt:auto; line-height:27.0000pt; mso-line-height-rule:exactly; \"><span style=\"mso-spacerun:\'yes\'; font-family:\'Times New Roman\'; mso-fareast-font-family:仿宋; font-size:14.0000pt; mso-font-kerning:0.0000pt; \">At&nbsp;present,&nbsp;Professor&nbsp;Li&nbsp;Yongwang&nbsp;has&nbsp;also&nbsp;served&nbsp;as&nbsp;vice&nbsp;chief&nbsp;editor&nbsp;of&nbsp;&ldquo;Journal&nbsp;of&nbsp;Fuel&nbsp;Chemistry&rdquo;,&nbsp;editorial&nbsp;advisory&nbsp;of&nbsp;&ldquo;Journal&nbsp;of&nbsp;Chemistry&nbsp;and&nbsp;Engineering&rdquo;,&nbsp;director&nbsp;of&nbsp;coal&nbsp;indirect&nbsp;liquefaction&nbsp;of&nbsp;National&nbsp;Engineering&nbsp;Laboratory&nbsp;and&nbsp;deputy&nbsp;director&nbsp;of&nbsp;State&nbsp;Key&nbsp;Laboratory&nbsp;of&nbsp;Coal&nbsp;Conversion&nbsp;and&nbsp;served&nbsp;as&nbsp;member&nbsp;of&nbsp;CPPCC&nbsp;of&nbsp;Shanxi&nbsp;Province.&nbsp;In&nbsp;2007,&nbsp;Professor&nbsp;Li&nbsp;was&nbsp;honored&nbsp;as&nbsp;&ldquo;Excellent&nbsp;Labor&nbsp;Model&nbsp;of&nbsp;Shanxi&nbsp;Province&rdquo;;&nbsp;in&nbsp;2008,&nbsp;he&nbsp;was&nbsp;honored&nbsp;as&nbsp;a&nbsp;&ldquo;National&nbsp;Outstanding&nbsp;Chemical&nbsp;Science&nbsp;and&nbsp;Technology&nbsp;Worker&rdquo;&nbsp;by&nbsp;China&nbsp;Petroleum&nbsp;and&nbsp;Chemical&nbsp;Industry&nbsp;Association;&nbsp;also,&nbsp;in&nbsp;2008,&nbsp;he&nbsp;won&nbsp;the&nbsp;title&nbsp;of&nbsp;&ldquo;Taiyuan&nbsp;Outstanding&nbsp;Entrepreneur&nbsp;in&nbsp;High-tech&nbsp;Zone&rdquo;;&nbsp;in&nbsp;2009,&nbsp;he&nbsp;won&nbsp;the&nbsp;&ldquo;National&nbsp;May&nbsp;1</span><span style=\"mso-spacerun:\'yes\'; font-family:\'Times New Roman\'; mso-fareast-font-family:仿宋; font-size:14.0000pt; mso-font-kerning:0.0000pt; vertical-align:super; \">st</span><span style=\"mso-spacerun:\'yes\'; font-family:\'Times New Roman\'; mso-fareast-font-family:仿宋; font-size:14.0000pt; mso-font-kerning:0.0000pt; \">&nbsp;Labor&nbsp;Medal&rdquo;;&nbsp;in&nbsp;2010,&nbsp;he&nbsp;won&nbsp;the&nbsp;title&nbsp;of&nbsp;&ldquo;National&nbsp;Advanced&nbsp;Worker&rdquo;;&nbsp;in&nbsp;2011,&nbsp;he&nbsp;was&nbsp;honored&nbsp;as&nbsp;&ldquo;Shanxi&nbsp;Top-Notch&nbsp;Science&nbsp;and&nbsp;Technology&nbsp;Innovation&nbsp;Pacesetter&rdquo;;&nbsp;in&nbsp;2014,&nbsp;he&nbsp;won&nbsp;&ldquo;Applied&nbsp;Science&nbsp;Award&rdquo;&nbsp;presented&nbsp;by&nbsp;Zhou&nbsp;Guangzhao&nbsp;Foundation.</span><span style=\"mso-spacerun:\'yes\'; font-family:\'Times New Roman\'; mso-fareast-font-family:仿宋; font-size:14.0000pt; mso-font-kerning:0.0000pt; \"><o:p></o:p></span></p>\r\n</body>\r\n</html>\r\n', '1', '', '402881e851378060015137aec4f8001c', '1', '1', '', 'http://114.242.195.204:8081/upload/files/images/%E5%85%B3%E4%BA%8E%E6%88%91%E4%BB%AC_%E9%9B%86%E5%9B%A2%E6%A6%82%E5%86%B5_%E7%AE%A1%E7%90%86%E5%B1%82_0626_03.jpg', '');
INSERT INTO `zk_en_content_info` VALUES ('ff808081515b3fc401515b4cbfb20011', 'Wholly owned subsidiary of Synfuels China Technology Co., Ltd', '2015-12-01 10:08:55', '<html>\r\n<head>\r\n	<title></title>\r\n</head>\r\n<body>\r\n<p class=\"MsoNormal\"><span style=\"mso-spacerun:\'yes\'; font-family:\'Times New Roman\'; mso-fareast-font-family:仿宋; font-size:14.0000pt; mso-font-kerning:0.0000pt; \">Synfuels&nbsp;China&nbsp;Engineering</span><span style=\"mso-spacerun:\'yes\'; font-family:\'Times New Roman\'; mso-fareast-font-family:仿宋; font-weight:bold; font-size:14.0000pt; mso-font-kerning:0.0000pt; \">&nbsp;</span><span style=\"mso-spacerun:\'yes\'; font-family:\'Times New Roman\'; mso-fareast-font-family:仿宋; font-size:14.0000pt; mso-font-kerning:0.0000pt; \">Holdings&nbsp;Co.,&nbsp;Ltd.&nbsp;is&nbsp;a&nbsp;wholly&nbsp;owned&nbsp;subsidiary&nbsp;of&nbsp;Synfuels&nbsp;China&nbsp;Technology&nbsp;Co.,&nbsp;Ltd.&nbsp;Synfuels&nbsp;China&nbsp;is&nbsp;the&nbsp;main&nbsp;R&amp;D&nbsp;force&nbsp;of&nbsp;China&rsquo;s&nbsp;coal&nbsp;chemical&nbsp;technology.&nbsp;Over&nbsp;the&nbsp;years,&nbsp;with&nbsp;the&nbsp;support&nbsp;of&nbsp;Ministry&nbsp;of&nbsp;Science&nbsp;and&nbsp;Technology,&nbsp;the&nbsp;National&nbsp;Development&nbsp;and&nbsp;Reform&nbsp;Commission,&nbsp;the&nbsp;Chinese&nbsp;Academy&nbsp;of&nbsp;Sciences,&nbsp;local&nbsp;governments&nbsp;and&nbsp;related&nbsp;enterprises&nbsp;as&nbsp;well&nbsp;as&nbsp;its&nbsp;own&nbsp;long-term&nbsp;research&nbsp;and&nbsp;diligent&nbsp;work,&nbsp;Synfuels&nbsp;China&nbsp;built&nbsp;China&rsquo;s&nbsp;first&nbsp;set&nbsp;of&nbsp;test&nbsp;platform&nbsp;with&nbsp;independent&nbsp;IPR&nbsp;of&nbsp;10&nbsp;million&nbsp;tons/year&nbsp;of&nbsp;coal&nbsp;based&nbsp;slurry&nbsp;bed&nbsp;synthetic&nbsp;oil&nbsp;in&nbsp;CAS&nbsp;Shanxi&nbsp;Institute&nbsp;of&nbsp;Coal&nbsp;Chemistry&nbsp;Test&nbsp;Base&nbsp;in&nbsp;2002.&nbsp;In&nbsp;April&nbsp;2006,&nbsp;Synfuels&nbsp;China&nbsp;formed&nbsp;coal&nbsp;based&nbsp;synthetic&nbsp;liquid&nbsp;fuel&nbsp;technology&nbsp;with&nbsp;independent&nbsp;IPR&nbsp;and&nbsp;technical&nbsp;basis&nbsp;and&nbsp;support&nbsp;ability&nbsp;of&nbsp;industrial&nbsp;scale.</span></p>\r\n</body>\r\n</html>\r\n', '1', '', '402881e851378060015137af3cb8001e', '1', '2', '', '', '');
