INSERT INTO `zk_en_content_info` VALUES ('ff808081515b3fc401515b5360fa002a', '', '2015-12-01 10:16:09', '<html>\r\n<head>\r\n	<title></title>\r\n</head>\r\n<body>\r\n<p class=\"MsoNormal\">The Engineering Company is home to fine talents structure and professionals of engineering design, engineering consulting, project management and control, procurement and construction management. There are 402 staff, including 391technical personnel, among which 92 people with advanced title, 138 people with intermediate title and 144 people with primary title. As for the staff&rsquo;s educational background, 2 people are doctoral degree, 115 people are master degree, 256 people are schlor degree and 26 people are college degree.</p>\r\n</body>\r\n</html>\r\n', '1', '', '402881e851378060015137b08aad0028', '1', '1', '', 'http://114.242.195.204:81/upload/files/images/zgs-text1.png', '');
