INSERT INTO `zk_recruit_info` VALUES ('ff8080814e75b72c014e75e0b8a70029', '研发类', '研发工程师', '<html>\r\n<head>\r\n	<title></title>\r\n</head>\r\n<body><span style=\"color: rgb(76, 76, 76); font-family: \'Microsoft YaHei\', 宋体; font-size: 16px; line-height: 34px; background-color: rgb(238, 238, 238);\">催化反应工艺、工业催化剂深度表征、重油加工研发、煤热解及自由基化学、分子筛合成及优化、重整煤焦油加氢、甘油下游、大型仪器表征测试研发、化工过程热力学、化工过程模拟编程、煤炭液化。</span></body>\r\n</html>\r\n', '<html>\r\n<head>\r\n	<title></title>\r\n</head>\r\n<body><span style=\"color: rgb(76, 76, 76); font-family: \'Microsoft YaHei\', 宋体; font-size: 16px; line-height: 34px; background-color: rgb(238, 238, 238);\">硕士研究生及以上学历；专业对口研究方向一致；211、985高校或科研院所教育背景优先。</span></body>\r\n</html>\r\n', '2016-01-11 19:54:46', '1');
INSERT INTO `zk_recruit_info` VALUES ('ff8080814edff972014ee0052ab2000c', '工程设计类', '高级设计师', '<html>\r\n<head>\r\n	<title></title>\r\n</head>\r\n<body>\r\n<div><span style=\"color: rgb(76, 76, 76); font-family: \'Microsoft YaHei\', 宋体; font-size: 16px; line-height: 34px; background-color: rgb(238, 238, 238);\">化工工艺设计、炼油工艺设计、储运设计、管道材料、管道设计、管道应力分析、结构设计、建筑设计、给排水设计、环保设计、劳动安全、暖通设计、电气设计、总图设计、仪表设计、化工机械设计、造价、概预算、费控、化工设备采购、文档控制、计划控制。</span></div>\r\n</body>\r\n</html>\r\n', '<html>\r\n<head>\r\n	<title></title>\r\n</head>\r\n<body><span style=\"color: rgb(76, 76, 76); font-family: \'Microsoft YaHei\', 宋体; font-size: 16px; line-height: 34px; background-color: rgb(238, 238, 238);\">（1）本科，工程师以上职称、相关专业；</span><br style=\"color: rgb(76, 76, 76); font-family: \'Microsoft YaHei\', 宋体; font-size: 16px; line-height: 34px; background-color: rgb(238, 238, 238);\" />\r\n<span style=\"color: rgb(76, 76, 76); font-family: \'Microsoft YaHei\', 宋体; font-size: 16px; line-height: 34px; background-color: rgb(238, 238, 238);\">（2）8年以上化工设计院或工程公司专业工作经验；</span><br style=\"color: rgb(76, 76, 76); font-family: \'Microsoft YaHei\', 宋体; font-size: 16px; line-height: 34px; background-color: rgb(238, 238, 238);\" />\r\n<span style=\"color: rgb(76, 76, 76); font-family: \'Microsoft YaHei\', 宋体; font-size: 16px; line-height: 34px; background-color: rgb(238, 238, 238);\">（3）具有专业负责人工作经验。</span></body>\r\n</html>\r\n', '2015-12-04 03:24:37', '1');
